con:request.con:request:
fetch('https://jsonplaceholder.typicode.com/posts/1', {
  method: 'DELETE'
})
.then(response => {
  if (response.ok) {
    console.log('Resource deleted successfully');
  } else {
    console.log('Error deleting resource');
  }
})

con:configuration.scriptText:
import groovy.json.JsonSlurper

// 1) HTTP status
assert messageExchange.responseStatusCode == 200

// 2) Parse JSON
def json = new JsonSlurper().parseText(messageExchange.responseContent)

// 3) Basic checks (for /posts/{id} with id=1)
assert json.id == 1
assert (json.title as String)?.trim()
log.info "scrript assertion is completed"

con:restRequest.con:request:
fetch('https://jsonplaceholder.typicode.com/posts/1', {
  method: 'DELETE'
})
.then(response => {
  if (response.ok) {
    console.log('Resource deleted successfully');
  } else {
    console.log('Error deleting resource');
  }
})

